import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
import { Button, Dialog, DialogContent, DialogTitle, TextField } from '@mui/material';

function App() {
  const [openCadastro,setOpenCadastro] = useState<boolean>(false);
  const colunas: GridColDef[] = [
    {
      field: "id", headerName: "ID", width: 20,
    },
    {
      field: "nome", headerName: "NOME", width: 150,
    }
  ]

  const linhas = [
    {id: 1, nome: "Douglas"},
    {id: 2, nome: "Junior"}
  ]

  const closeCadastro = () => {
    setOpenCadastro(false); 
  }


  return (
   <div style={{maxWidth: "100%"}}>
    <header style={{display:"flex"}}>
      <div style={{display: "flex", width:"100%", padding:"27px"}}>
      <h1>
      <img style={{display:"block", width: "100px", height:"59px"}}src="./log-senac.png" />
      </h1>
      <div style={{display:"flex", alignItems:"center", justifyContent:"center", width: "100%"}}>
        <p style={{fontSize: "35px", color: "#004587",}}>
          App Layout
        </p>
        </div>
      </div>
    </header>
    <div style={{display: "flex", backgroundImage: "url(http://localhost:3000/slide_senac.jpg)", height:"200px", backgroundSize: "cover", alignItems:"center", padding: "0 40px"}}>
      <p style={{color:"white", fontSize: "24px"}}>
        Senac
      </p>
      <div style={{display: "flex", padding: "5px"}}>
       <Button
        variant="contained"
        onClick={()=> {
          setOpenCadastro(true);
        }}>Adicionar Cliente</Button>
        </div>
        <div style={{display:"flex"}}>
          <DataGrid
            rows = {linhas}
            columns={colunas}
            checkboxSelection
            initialState={{
            pagination:{
            paginationModel:{
              pageSize: 5 
            }
          }
        }} />  
    </div>
    <Dialog open={openCadastro} onClose={closeCadastro}>
      <DialogTitle>Tela de cadastro</DialogTitle>
      <DialogContent>
        <div style={{width: "100%"}}>
          <TextField margin="dense" fullWidth variant="outlined" label="ID"/>
        </div>
        <div style={{width: "100%"}}>
          <TextField margin="dense" fullWidth variant="outlined" label="Nome"/>
        </div>
      </DialogContent>
    </Dialog>
   </div>
   </div>
  );
}
export default App;
