import React from 'reactjs';
import logo from './logo.svg';
import './App.css';
import { DataGrid, GridColDef } from '@mui/x-data-grid';

function App() {
  const colunas: GridColDef[]=[{field: "id", headerName: "ID",width:20 },{field:"nome",headerName:"Nome", width:150,}]
  const linhas =[{
    id:1, nome: "Roberto"
  },{
    id:2,nome:"Alice"
  }]

  return (
    <div style ={{maxWidth:"100%"}}>
      <header style ={{display: "flex"}}>
        <div style={{display:"flex", width: "100%", padding:"27px",}}>
        <h1>
          <img style={{display: "block", width: "100px", height:"100px"}}src="./OIP.jpeg"/>
        </h1>
        <div style={{display: "flex",alignItems: "center", justifyContent: "center", width:"100%"}}>
          <p style={{fontSize: "35px", color: "#004587"}}> APPato</p>
        </div>
        </div>
      </header>
      <div style = {{display: "flex" ,backgroundImage: "url(http://localhost:3000/R.jpeg)", height: "300px", backgroundSize: "cover",alignItems:"center",padding:"0 40px"}}>
      <p style = {{color: "`black`", fontSize:"24px"}}>patodos</p>
    </div>
    <div style={{display: "flex"}}>
      <DataGrid
        rows = {linhas}
        columns={colunas}
        checkboxSelection
        initialState={{
          pagination:{
            paginationModel:{
              pageSize: 5
            }
          }
        }}
        />
        </div>
    </div>
  );
}

export default App;
